mapper.update:
UPDATE lol_board SET
b_title = #{b_title}, b_cont= #{b_cont}, b_date = sysdate WHERE b_num
= #{b_num}

mapper.select:
SELECT b.b_num, b.b_title, b.b_id, b.b_date, b.b_hits, b.b_likes, COUNT(r.r_num) AS reply_count FROM lol_board b LEFT JOIN lol_board_reply r ON b.b_num = r.r_board_num
AND b.b_category = #{b_category}
GROUP BY b.b_num, b.b_title, b.b_id, b.b_date, b.b_hits, b.b_likes ORDER BY b.b_num DESC OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

SELECT COUNT(*) FROM lol_board_likes
WHERE b_num = #{b_num}
AND m_id = #{m_id}

mapper.delete:
DELETE FROM
lol_board_likes
WHERE b_num = #{b_num} AND m_id = #{m_id}
